CREATE TABLE IF NOT EXISTS `orders` (`id` INT AUTO_INCREMENT PRIMARY KEY,`name` VARCHAR(255) NOT NULL,`amount_in_stock` INT NOT NULL,`price` FLOAT NOT NULL,`date_of_creation` DATETIME NOT NULL,`status` VARCHAR(50) NOT NULL);

CREATE TABLE IF NOT EXISTS `order_items` (`id` INT AUTO_INCREMENT PRIMARY KEY,`value` FLOAT NOT NULL,`order_id` INT NOT NULL,`creation_date` DATETIME NOT NULL,`name` VARCHAR(50) NOT NULL,FOREIGN KEY (`order_id`) REFERENCES `orders`(`id`) ON DELETE CASCADE ON UPDATE CASCADE);